import axios from "axios";


const instance = axios.create({
    baseURL: 'http://emphasoft-test-assignment.herokuapp.com/'
});


export const authAPI = {
    login(username, password){
        instance.withCredentials = false;
        return axios.post('api-token-auth/', {username, password}).then(res => res.data).catch(error => error.request);
    }
}

export const usersAPI = {
    get(){
        instance.withCredentials = true;
        return instance.get("api/v1/users/").then(res => res.data).catch(error => error.request);
    }
}
