import React from 'react';
import style from './Authorization.module.css';
import {Field, reduxForm} from "redux-form";

const LoginForm = (props) => {
    return (
        <form onSubmit ={props.handleSubmit}>
            <Field type="text" autoComplete={"off"} validate={[requiredField, maxLengthCreator20]} name={"login"} placeholder="Логин" component={Textarea} />
            <Field type="password" validate={[requiredField, maxLengthCreator20]} name={"password"} placeholder="Пароль" component={TextareaPassword} />
            <Field type="checkbox" name={"rememberMe"} component={Checkbox} />
            <button className={style.btn_enter} >Вход</button>
            {props.error && <div className={style.error_message_enter}>
                {props.error}
            </div>}
        </form>
    );
}


const LoginReduxForm = reduxForm({form:"login"})(LoginForm)


const Authorization = (props) => {
    return (<div className={style.container}>
                <div className={style.enter}>

                </div>
            </div>);
}



export default Authorization;
